/* eslint-disable react-hooks/exhaustive-deps */
import React, { useCallback, useEffect, useState } from "react";
import PageContainer from "../../components/PageContainer";
import { CarsProps } from "../../utils/types/Cars";
import TableList from "./TableList";
import {
  Button,
  Dialog,
  DialogActions,
  DialogTitle,
  Slide,
} from "@mui/material";
import { TransitionProps } from "@mui/material/transitions";

const Transition = React.forwardRef(function Transition(
  props: TransitionProps & {
    children: React.ReactElement<any, any>;
  },
  ref: React.Ref<unknown>
) {
  return <Slide direction="up" ref={ref} {...props} />;
});
const Listings: React.FC = () => {
  const [data, setData] = useState<CarsProps[]>([]);
  const [dataDefault, setDataDefault] = useState<CarsProps[]>([]);
  const [open, setOpen] = useState(false);
  const [deleteCar, setDeleteCar] = useState("");

  useEffect(() => {
    const getData = async () => {
      const response = await fetch("/api/cars");
      const responseData = await response.json();

      setData(responseData);
      setDataDefault(responseData);
    };

    getData();
  }, []);

  const handleSelectDelete = useCallback(
    (value: string) => {
      setDeleteCar(value);
      setOpen(true);
    },
    [deleteCar, open]
  );

  const handleClose = useCallback(() => {
    setDeleteCar("");
    setOpen(false);
  }, []);

  const handleOnDelete = useCallback(async () => {
    setOpen(false);
    const requestOptions = {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        id: deleteCar,
      }),
    };

    const despesas = await fetch("/api/cars", requestOptions);
    const { status } = await despesas.json();

    console.log(status);

    window.location.reload();
  }, []);

  return (
    <PageContainer>
      <TableList data={data} onDelete={handleSelectDelete} />

      <Dialog
        open={open}
        TransitionComponent={Transition}
        keepMounted
        onClose={handleClose}
        aria-describedby="alert-dialog-slide-description"
      >
        <DialogTitle>Deseja realmente remover?</DialogTitle>
        <DialogActions>
          <Button onClick={handleClose}>Cancelar</Button>
          <Button onClick={handleOnDelete}>Deletar</Button>
        </DialogActions>
      </Dialog>
    </PageContainer>
  );
};

export default Listings;
