/* eslint-disable react-hooks/exhaustive-deps */
import React, {
  FunctionComponent,
  useCallback,
  useEffect,
  useState,
} from "react";

import Box from "@mui/material/Box";
import Tab from "@mui/material/Tab";
import TabContext from "@mui/lab/TabContext";
import TabList from "@mui/lab/TabList";
import TabPanel from "@mui/lab/TabPanel";
import PageContainer from "../../components/PageContainer";
import Form from "../../components/Form";
import carsEmpty from "../Adding/utils/carsEmpty";
import { Button } from "@mui/material";

import "./index.css";
import ListingsActions from "./Components/ListingsActions";
import { CarsProps } from "../../utils/types/Cars";
import format from "../../utils/format/priceBrazil";

const EditCar: FunctionComponent<{}> = (props) => {
  const [data, setData] = useState<CarsProps>();
  const [valueTab, setValueTab] = useState("1");
  const [despesas, setDespesas] = useState([]);

  useEffect(() => {
    const id = document.location.pathname.split("/editar/")[1];

    async function getInfo() {
      const car = await fetch(`/api/car/${id}`);
      const responseData = await car.json();

      const despesasCar = await fetch(`/api/despesas/${id}`);
      const responsedespesasCar = await despesasCar.json();

      setData(responseData);
      setDespesas(responsedespesasCar);
    }

    getInfo();
  }, []);

  const handleChangeTab = (event: React.SyntheticEvent, newValue: string) => {
    setValueTab(newValue);
  };

  const handleSave = useCallback(async (value: any) => {
    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(value),
    };
  }, []);

  const onDelete = useCallback(async (value: number) => {
    const requestOptions = {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
    };
    const despesas = await fetch(`/api/despesas/${value}`, requestOptions);
    const { status } = await despesas.json();

    if (status !== 200) {
    }

    window.location.reload();
  }, []);

  const handleEditDespesas = useCallback((id: number) => {
    console.log(id);
  }, []);

  const total =
    despesas.length > 0
      ? despesas
          .map(({ value }) => parseFloat(value))
          .reduce((accumulator, value) => accumulator + value, 0)
      : 0;

  return (
    <>
      <PageContainer>
        <TabContext value={valueTab}>
          <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
            <TabList
              onChange={handleChangeTab}
              aria-label="lab API tabs example"
            >
              <Tab label="Informações do carro" value="1" />
              <Tab label="Lista de despesas" value="2" />
            </TabList>
          </Box>
          <TabPanel value="1">
            {data && <Form value={data} onSave={handleSave} />}
          </TabPanel>
          <TabPanel value="2">
            <div className="container-total">
              <span>Total de despesas {format(total)}</span>
              <Button variant="outlined">+ Cadastrar nova despesa</Button>
            </div>
            {despesas && (
              <ListingsActions
                despesas={despesas}
                onDelete={onDelete}
                onEdit={handleEditDespesas}
              />
            )}
          </TabPanel>
        </TabContext>
      </PageContainer>
    </>
  );
};

export default EditCar;
