/* eslint-disable react-hooks/exhaustive-deps */
import React, { useCallback, useEffect, useState } from "react";
import { CarsProps } from "../../utils/types/Cars";

import PageContainer from "../../components/PageContainer";
import ModalRemove from "../../components/ModalRemove";
import TableList from "./TableList";
import FilterModal from "./FilterModal";

import AddIcon from "@mui/icons-material/Add";
import FilterAltOutlinedIcon from "@mui/icons-material/FilterAltOutlined";
import { Button, Typography } from "@mui/material";

import "./index.css";

const Listings: React.FC = () => {
  const [data, setData] = useState<CarsProps[]>([]);
  const [dataDefault, setDataDefault] = useState<CarsProps[]>([]);
  const [open, setOpen] = useState(false);
  const [deleteCar, setDeleteCar] = useState("");
  const [openFilter, setOpenFilter] = useState(false);

  useEffect(() => {
    const getData = async () => {
      const response = await fetch("/api/cars");
      const responseData = await response.json();

      setData(responseData);
      setDataDefault(responseData);
    };

    getData();
  }, []);

  const handleSelectDelete = useCallback(
    (value: string) => {
      setDeleteCar(value);
      setOpen(true);
    },
    [deleteCar, open]
  );

  const handleClose = useCallback(() => {
    setDeleteCar("");
    setOpen(false);
  }, [deleteCar, open]);

  const handleCloseFilter = useCallback(() => {
    setOpenFilter(false);
  }, [openFilter]);

  const handleOnDelete = useCallback(async () => {
    const requestOptions = {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        id: deleteCar,
      }),
    };

    setOpen(false);

    const despesas = await fetch("/api/cars", requestOptions);
    await despesas.json();

    window.location.reload();
  }, [open]);

  const handleOpenFilter = useCallback(() => {
    setOpenFilter(true);
  }, [openFilter]);

  return (
    <PageContainer>
      <section>
        <Typography variant="h6">Estoque</Typography>
        <div className="actions-filters">
          <Button
            variant="outlined"
            onClick={handleOpenFilter}
            startIcon={<FilterAltOutlinedIcon />}
          >
            Filtrar
          </Button>
          <Button
            variant="contained"
            onClick={() => window.location.assign("/adicionar")}
            endIcon={<AddIcon />}
          >
            Adicionar carro
          </Button>
        </div>
      </section>
      <TableList data={data} onDelete={handleSelectDelete} />

      <FilterModal
        open={openFilter}
        data={data}
        onClose={handleCloseFilter}
        onFilter={() => null}
      />

      <ModalRemove
        open={open}
        onClose={handleClose}
        onDelete={handleOnDelete}
      />
    </PageContainer>
  );
};

export default Listings;
