interface Response {
  data: {
    user: {
      name: string;
      email: string;
    };
    token: string;
  };
}

export async function login(userData: object) {
  try {
    console.log( JSON.stringify(userData))
    const response = await fetch('/api/auth', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userData),
    });

    if (!response.ok) {
      throw new Error('Erro na requisição'); // Trate erros de resposta não-ok
    }

    const data = await response.json();

    // Faça algo com os dados retornados pela API

    console.log(data)
    console.log(data.user.name);
    console.log(data.user.email);

    return data;
  } catch (error) {
    // Trate qualquer erro que possa ocorrer durante a requisição
    console.error(error);
  }
}

export const defaults = {
  headers: {
    Authorization: '',
  },
};
