/* eslint-disable react-hooks/exhaustive-deps */
import React, { useCallback, useEffect, useState } from "react";
import PageContainer from "../../components/PageContainer";
import { CarsProps } from "../../utils/types/Cars";
import TableList from "./TableList";

import ModalRemove from "../../components/ModalRemove";
import AddIcon from "@mui/icons-material/Add";
import FilterAltOutlinedIcon from "@mui/icons-material/FilterAltOutlined";
import { Button, Card, CardContent, Typography } from "@mui/material";

const Listings: React.FC = () => {
  const [data, setData] = useState<CarsProps[]>([]);
  const [dataDefault, setDataDefault] = useState<CarsProps[]>([]);
  const [open, setOpen] = useState(false);
  const [deleteCar, setDeleteCar] = useState("");

  useEffect(() => {
    const getData = async () => {
      const response = await fetch("/api/cars");
      const responseData = await response.json();

      setData(responseData);
      setDataDefault(responseData);
    };

    getData();
  }, []);

  const handleSelectDelete = useCallback(
    (value: string) => {
      console.log(value);
      setDeleteCar(value);
      setOpen(true);
    },
    [deleteCar, open]
  );

  const handleClose = useCallback(() => {
    setDeleteCar("");
    setOpen(false);
  }, [deleteCar, open]);

  const handleOnDelete = useCallback(async () => {
    const requestOptions = {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        id: deleteCar,
      }),
    };

    setOpen(false);

    const despesas = await fetch("/api/cars", requestOptions);
    await despesas.json();

    window.location.reload();
  }, [open]);

  return (
    <PageContainer>
      <section>
        <Typography variant="h6">Estoque</Typography>
        <div>
          <Button variant="outlined" startIcon={<FilterAltOutlinedIcon />}>
            Filtrar
          </Button>
          <Button
            variant="contained"
            onClick={() => window.location.assign("/adicionar")}
            endIcon={<AddIcon />}
          >
            Adicionar carro
          </Button>
        </div>
      </section>
      <TableList data={data} onDelete={handleSelectDelete} />

      <ModalRemove
        open={open}
        onClose={handleClose}
        onDelete={handleOnDelete}
      />
    </PageContainer>
  );
};

export default Listings;
