import React, { useEffect, useState } from "react";
import PageContainer from "../../components/PageContainer";
import ContainerValue from "./components/ContainerValue";
import { Props } from "./types";
import {
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from "@mui/material";

const Home: React.FC = () => {
  const [data, setData] = useState<Props>({
    vendido: [],
    compra: [],
  });

  useEffect(() => {
    async function getInfo() {
      const response = await fetch("/api/cars/full");
      const responseData = await response.json();

      setData(responseData);
    }

    getInfo();
  }, []);

  // useEffect(() => {
  //   if (data.compra.length === 0) {
  //     const getInfoMonth = async () => {
  //       const response = await fetch("/api/cars/full");
  //       const responseData = await response.json();

  //       console.log(responseData);
  //       setData(responseData);
  //     };

  //     getInfoMonth();
  //   }
  // }, []);

  return (
    <>
      <PageContainer>
        <div>
          <ContainerValue
            value={30000}
            name="Comprados no mÃªs"
            className="compra"
          />
          <TableContainer component={Paper}>
            <Table className="min-w-max w-full table-auto">
              <TableHead>
                <TableRow>
                  <TableCell>Marca - Modelo</TableCell>
                  <TableCell>Data da compra</TableCell>
                  <TableCell>Valor</TableCell>
                </TableRow>
              </TableHead>
              <TableBody className="text-gray-600 text-sm font-light">
                {data.compra.map(
                  ({ modelo, marca, dt_compra, valor_compra, id_car }) => (
                    <TableRow
                      className="border-b border-gray-200 hover:bg-gray-100"
                      onClick={() =>
                        window.location.assign(`/editar/${id_car}`)
                      }
                    >
                      <TableCell className="py-3 px-6 text-left whitespace-nowrap">
                        <span className="font-medium">{`${marca} - ${modelo}`}</span>
                      </TableCell>
                      <TableCell className="py-3 px-6 text-left">
                        <span>{dt_compra} </span>
                      </TableCell>
                      <TableCell className="py-3 px-6 text-left">
                        <span>
                          R$
                          {valor_compra}
                        </span>
                      </TableCell>
                    </TableRow>
                  )
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </div>
      </PageContainer>
    </>
  );
};

export default Home;
