interface Response {
  data: {
    user: {
      name: string;
      email: string;
    };
    token: string;
  };
}

export async function login(userData: object) {
  try {
    const response = await fetch('/api/auth', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userData),
    });

    if (!response.ok) {
      throw new Error('Erro na requisição'); // Trate erros de resposta não-ok
    }

    const data = await response.json();

    return data;
  } catch (error) {
  }
}

export const defaults = {
  headers: {
    Authorization: '',
  },
};
